        // Products
        productRepository.save(new Product(1L, "Wodka Gorbatschow 200ml", "https://iili.io/HwltObI.jpg", "Der Wodka Gorbatschow ist ein hochwertiger Wodka aus Deutschland, der aus feinstem Weizen hergestellt wird. Er zeichnet sich durch sein mildes und zugleich würziges Aroma aus. Durch seine vielseitige Verwendungsmöglichkeiten eignet er sich perfekt zum Mixen von Cocktails oder als Begleiter zu einem eleganten Dinner. Ein absoluter Klassiker unter den Wodkas!", new BigDecimal(3), true, Category.CategoryType.SPIRITS));
        productRepository.save(new Product(2L, "Ramero Rum Blanco 3x 500ml", "https://iili.io/Hwltkxt.webp", "Der Ramero Rum Blanco ist ein erfrischender Rum aus der Karibik, der aus besten Zuckerrohrmelassen hergestellt wird. Er besticht durch seine klare, kristalline Farbe und sein leicht süßes Aroma mit einem Hauch von Vanille und tropischen Früchten. Der Rum eignet sich hervorragend zum Mixen von Cocktails oder als Basis für erfrischende Longdrinks. Ein idealer Begleiter für warme Sommerabende oder entspannte Stunden auf der Terrasse.", new BigDecimal(49.99), true, Category.CategoryType.SPIRITS));
        productRepository.save(new Product(3L, "Sprizzero Bitterorange 10x 300ml", "https://iili.io/Hwlt8sn.jpg", "Der Sprizzero Bitterorange ist ein erfrischendes und belebendes Getränk, das aus natürlichen Zutaten hergestellt wird. Er vereint den herben Geschmack von Bitterorangen mit einer angenehmen Süße und einem Hauch von Kräutern. Der Sprizzero Bitterorange eignet sich perfekt als Aperitif oder als Begleiter zu einem entspannten Abend mit Freunden. Ein unverwechselbares Geschmackserlebnis, das Sie sich nicht entgehen lassen sollten.", new BigDecimal(12.00), true, Category.CategoryType.SPIRITS));
        productRepository.save(new Product(4L, "Tequila Herencia de Plata 700ml", "https://iili.io/HwltUfs.jpg", "Herencia de Plata ist ein preisgekrönter Tequila aus 100% blauer Agave, der in Mexiko produziert wird. Er überzeugt durch sein intensives Aroma mit einem Hauch von Zimt und Vanille und seinen ausgewogenen Geschmack mit einer leichten Schärfe. Der Tequila eignet sich hervorragend zum Genießen pur oder als Basis für Tequila-Cocktails. Herencia de Plata ist ein Premium-Tequila, der für höchste Qualität und Handwerkskunst steht.", new BigDecimal(23.00), true, Category.CategoryType.SPIRITS));
        productRepository.save(new Product(5L, "Steiger Whisky 700ml", "https://iili.io/Hwltrgf.jpg", "Der Steiger Whisky ist ein hochwertiger Single Malt Whisky aus Österreich, der aus erlesenen Gerstenmalzen und kristallklarem Bergquellwasser destilliert wird. Er reift mindestens 3 Jahre in Eichenfässern und überzeugt durch sein intensives Aroma mit einer angenehmen Rauchnote und einer subtilen Süße. Der Steiger Whisky eignet sich perfekt als Geschenk für Whiskyliebhaber oder zum Genießen in gemütlicher Atmosphäre. Ein authentischer Whisky, der für Qualität und Genuss auf höchstem Niveau steht.", new BigDecimal(25), true, Category.CategoryType.SPIRITS));
        productRepository.save(new Product(6L, "Coca Cola 335ml", "https://iili.io/Hwl4H5F.jpg", "Coca Cola ist ein erfrischendes Softgetränk, das seit 1886 weltweit bekannt und beliebt ist. Es wird aus natürlichen Zutaten wie Zucker, Koffein und natürlichen Aromen hergestellt und hat einen unverwechselbaren Geschmack. Coca Cola ist vielseitig einsetzbar und eignet sich hervorragend als Durstlöscher oder als Basis für leckere Cocktailkreationen. Ein Klassiker unter den Softdrinks, der unverwechselbar erfrischend schmeckt.", new BigDecimal(2), true, Category.CategoryType.NONALCOHOLIC));
